import React from 'react';
import Carousel from 'react-native-snap-carousel';

import Header from './components/Header/Header';
import Footer from './components/Footer/Footer';
import './App.css';

function App() {
  const products = [
    { id: 1, name: 'Produto 1', price: 'R$ 50,00', image: 'https://via.placeholder.com/150' },
    { id: 2, name: 'Produto 2', price: 'R$ 70,00', image: 'https://via.placeholder.com/150' },
    { id: 3, name: 'Produto 3', price: 'R$ 100,00', image: 'https://via.placeholder.com/150' },
    { id: 4, name: 'Produto 4', price: 'R$ 120,00', image: 'https://via.placeholder.com/150' },
    { id: 5, name: 'Produto 5', price: 'R$ 150,00', image: 'https://via.placeholder.com/150' },
  ];

  const renderItem = ({ item }) => {
    return (
      <div className="carousel-item" onClick={() => alert(`Você clicou em ${item.name}`)}>
        <img src={item.image} alt={item.name} className="carousel-image" />
        <h2 className="product-name">{item.name}</h2>
        <p className="product-price">{item.price}</p>
      </div>
    );
  };

  return (
    <div className="App">
      <Header />
      <main>
        <h1>PRODUTOS</h1>
        <Carousel
          data={products}
          renderItem={renderItem}
          sliderWidth={800}
          itemWidth={300}
          loop={true}
        />
      </main>
      <Footer />
    </div>
  );
}

export default App;
